import React,{useEffect,useState} from 'react'
import Events from './Events'
import "./index.css"
import DeleteOutlineIcon from '@mui/icons-material/DeleteOutline';
import AddIcon from '@mui/icons-material/Add';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import TextField from '@mui/material/TextField';
import OutlinedInput from '@mui/material/OutlinedInput';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import BalanceIcon from '@mui/icons-material/Balance';
import Final from './Final';
import { useHistory } from 'react-router-dom'
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';


export default function Organizations({name,id,fun}) {
  const[expenses,setExpenses] = useState();
  const history = useHistory();
  const [open, setOpen] = useState(false);
  const[split, setSplit]=useState(false);
  const [totalExpense,setTotalExpense] = useState(0);
  const[finalSplit,setFinalSplit]=useState([]);
  const[users,setUsers] = useState();
  const curruser = JSON.parse(localStorage.getItem("user-info"))

  const[expName,setExpName] = useState();
  const[expAmt,setExpAmt] = useState();
  const[expPaidBy,setExpPaidBy] = useState();
  const[expGrp,setExpGrp] = useState();
  const [personName, setPersonName] = useState([]);
  const[selectUsers,setSelectUsers]= useState([]);
  var usrSplitBtw = [];


  useEffect( () => {
    async function fetchData() {
     let expenses= await fetch(`http://localhost:8080/groups/expenses/${id}`,{
      method:'GET',
      headers:{
          "Content-Type":"application/json",
          "Accept":"application/json",
      },});
      expenses = await expenses.json();
      setExpenses(expenses);
      for(var i=0;i<expenses.length;i++){
        setTotalExpense(totalExpense + expenses[i].expAmt);
      }
      if(users==null){ 
     let paidby= await fetch(`http://localhost:8080/groups/users/${id}`,{
      method:'GET',
      headers:{
          "Content-Type":"application/json",
          "Accept":"application/json",
      },});
      paidby = await paidby.json();
      setUsers(paidby);
      setSelectUsers(paidby);
    }
    }
    fetchData();

  },[]);


  const handleChange = (event) => {

    setPersonName(
      typeof event.target.value === 'string' ? event.target.value.split(',') : event.target.value,
    );
  };

  const openBox = (grpId) => {
    setSplit(false);
    setExpGrp(grpId);
    setOpen(true);
  }

async function gameOn() {
      if(split){
        setSplit(false)
      }
      else{
      try{
      let paidby=  await fetch(`http://localhost:8080/FinalSplit/${id}`,{
      method:'GET',
      headers:{
          "Content-Type":"application/json",
          "Accept":"application/json",
      },});
      paidby = await paidby.json();
      if(paidby != null)
      {
        setFinalSplit(paidby);
        console.log(paidby)
        setSplit(true);
      }
      else{
        return;
      }

    }
    catch(e){
      console.log(e);
    }
  }
  }

  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'white',
    boxShadow: 24,
    p: 4,
  };
  async function createExpense(){
    setOpen(false);
    //credentials
    for(var i=0;i<personName.length;i++){
      usrSplitBtw.push({"id":personName[i]})
    }

    let item = {
      "expName":expName,
      "expAmt":expAmt,
      "expPaidBy":expPaidBy,
      "usrSplitBtw":usrSplitBtw,
      "expGrp":{"id":expGrp}
    }
    console.log(item)

    try{
      let result= await fetch('http://localhost:8080/expense/create',{
      method:'POST',
      headers:{
          "Content-Type":"application/json",
      },
      body:JSON.stringify(item)
      });
      result = await result.json();
      if(result != null || !result.error)
      {
        setOpen(false); 
      }
      else{
        return;
      }
    }
    catch(e){
    console.log(e);
    }
    window.location.reload();

  }

  return (
    <>
    {split ?
    <>
    <div className='org-main'>
        <div className='org-heading'>
            <p className='org-name'>{name}</p>
            <div className='org-icons'>
